import React, { useState } from 'react';

// --- Data ---
// Yeh saara data pehle 'portfolioData.js' file mein tha.
// Ab isey yahan direct daal diya gaya hai.
const portfolioData = {
    name: "Yash Malviya",
    title: "Web Developer",
    headline: "Crafting Web Experiences with Code",
    about: "To strengthen my expertise in full-stack web development using Node.js, Express.js, MongoDB, HTML, CSS, and JavaScript, while continuously improving my problem-solving and algorithmic skills. I aim to build scalable, efficient, and user-friendly web applications that deliver real-world impact and reflect clean, maintainable code practices.",
    profileImg: "profile.jpeg",
    skills: {
        technical: ["C", "C++", "HTML5", "CSS3", "JavaScript", "Node.js", "Express.js", "MongoDB"],
        curriculum: ["Data structures & algorithms (DSA)", "Object oriented programming (OOP)"],
        interests: ["Software Development", "Competitive Programming", "IoT"],
        tools: ["GitHub", "VS Code", "Postman", "MS Word"]
    },
    projects: [
        {
            title: "Full Stack Blog Application",
            description: "Implemented full CRUD for posts with 20+ RESTful API endpoints, including like/unlike, nested comments with replies and large-scale media uploads using Cloudinary. Built a secure authentication system with JWT, OTP-based email verification and password reset functionality. Deployed on Render and Vercel.",
            tech: ["Node.js", "Express.js", "MongoDB", "JWT", "Cloudinary"],
            link: "https://blog-frontend-new-plum.vercel.app/"
        },
        {
            title: "Password Generator",
            description: "Developed a responsive front-end application using HTML, CSS and JavaScript. Implemented password generation logic supporting lengths from 1-20 characters and 4 distinct character sets. Integrated a 3-level password strength indicator and a one-click copy feature.",
            tech: ["HTML", "CSS", "JavaScript"],
            link: "https://yash1915.github.io/password_generator/"
        }
    ],
    experience: [
        {
            role: "Summer Intern",
            company: "Signal and Telecom Training Centre (N.E Railway, Gorakhpur)",
            duration: "June 2025 - July 2025",
            description: "Monitored 4+ railway signals and a data logger to ensure real-time safety. Diagnosed and reported a system fault, improving maintenance and reliability."
        }
    ],
    education: [
        {
            degree: "B.Tech (Electronics & Communication)",
            institution: "Madan Mohan Malaviya University of Technology, Gorakhpur",
            duration: "2022 - 2026",
            score: "8.02/10.0"
        },
        {
            degree: "Class XII (CBSE)",
            institution: "Little Flower House, Varanasi",
            duration: "2019 - 2020",
            score: "79.2%"
        }
    ],
    certificates: [
        {
            title: "Introduction to Internet of Things",
            issuer: "NPTEL, IIT Kharagpur",
            badge: "Elite Badge (85%)",
            link: "#"
        },
        {
            title: "Semiconductor Devices",
            issuer: "NPTEL, IISc Bangalore",
            badge: "Elite Badge (62%)",
            link: "#"
        }
    ],
    contact: {
        email: "yashmalviya1915@gmail.com",
        phone: "+91 9305045837",
        github: "https://github.com/yash1915",
        linkedin: "https://www.linkedin.com/in/yash-malviya-b4b7b2253/",
        resumeLink: "https://drive.google.com/drive/u/0/folders/1TjzJtzR1M2YdLOOnQS2_wxDXxzSnHYYk"
    }
};

// --- SVG Icons ---
// Yeh icons pehle 'icons.js' file mein the.
const MenuIcon = (props) => (
    <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={1.5} stroke="currentColor" {...props}>
        <path strokeLinecap="round" strokeLinejoin="round" d="M3.75 6.75h16.5M3.75 12h16.5m-16.5 5.25h16.5" />
    </svg>
);

const XIcon = (props) => (
    <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={1.5} stroke="currentColor" {...props}>
        <path strokeLinecap="round" strokeLinejoin="round" d="M6 18L18 6M6 6l12 12" />
    </svg>
);

const GithubIcon = (props) => (
  <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 16 16" fill="currentColor" {...props}>
    <path d="M8 0C3.58 0 0 3.58 0 8c0 3.54 2.29 6.53 5.47 7.59.4.07.55-.17.55-.38 0-.19-.01-.82-.01-1.49-2.01.37-2.53-.49-2.69-.94-.09-.23-.48-.94-.82-1.13-.28-.15-.68-.52-.01-.53.63-.01 1.08.58 1.23.82.72 1.21 1.87.87 2.33.66.07-.52.28-.87.51-1.07-1.78-.2-3.64-.89-3.64-3.95 0-.87.31-1.59.82-2.15-.08-.2-.36-1.02.08-2.12 0 0 .67-.21 2.2.82.64-.18 1.32-.27 2-.27.68 0 1.36.09 2 .27 1.53-1.04 2.2-.82 2.2-.82.44 1.1.16 1.92.08 2.12.51.56.82 1.27.82 2.15 0 3.07-1.87 3.75-3.65 3.95.29.25.54.73.54 1.48 0 1.07-.01 1.93-.01 2.2 0 .21.15.46.55.38A8.013 8.013 0 0016 8c0-4.42-3.58-8-8-8z"></path>
  </svg>
);

const LinkedinIcon = (props) => (
  <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="currentColor" {...props}>
    <path d="M20.5 2h-17A1.5 1.5 0 002 3.5v17A1.5 1.5 0 003.5 22h17a1.5 1.5 0 001.5-1.5v-17A1.5 1.5 0 0020.5 2zM8 19H5v-9h3zM6.5 8.25A1.75 1.75 0 118.25 6.5 1.75 1.75 0 016.5 8.25zM19 19h-3v-4.74c0-1.42-.6-1.93-1.38-1.93-.91 0-1.38.61-1.38 1.93V19h-3v-9h2.9v1.3a3.11 3.11 0 012.7-1.4c1.55 0 3.1 1.16 3.1 3.99z"></path>
  </svg>
);

const LinkIcon = (props) => (
  <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={1.5} stroke="currentColor" {...props}>
    <path strokeLinecap="round" strokeLinejoin="round" d="M13.19 8.688a4.5 4.5 0 011.242 7.244l-4.5 4.5a4.5 4.5 0 01-6.364-6.364l1.757-1.757m13.35-.622l1.757-1.757a4.5 4.5 0 00-6.364-6.364l-4.5 4.5a4.5 4.5 0 001.242 7.244" />
  </svg>
);


// --- Components ---
const Section = ({ id, title, children }) => (
    <section id={id} className="py-20 px-4 sm:px-6 md:px-10 lg:px-20 max-w-7xl mx-auto">
        <h2 className="text-3xl font-bold text-center text-slate-100 mb-12">{title}</h2>
        {children}
    </section>
);

const SkillBadge = ({ skill }) => (
    <div className="bg-slate-800 text-teal-300 py-2 px-4 rounded-lg font-medium">
        {skill}
    </div>
);

const Header = () => {
    const [isOpen, setIsOpen] = useState(false);
    const navLinks = ["about", "skills", "projects", "experience", "contact"];

    return (
        <header className="fixed top-0 left-0 right-0 z-50 bg-slate-900/80 backdrop-blur-sm">
            <div className="container mx-auto flex justify-between items-center p-4 text-slate-100">
                <a href="#" className="text-2xl font-bold tracking-wider">
                    Y<span className="text-teal-400">M</span>
                </a>
                <nav className="hidden md:flex space-x-8">
                    {navLinks.map(link => (
                        <a key={link} href={`#${link}`} className="capitalize hover:text-teal-400 transition-colors duration-300">
                            {link}
                        </a>
                    ))}
                </nav>
                <div className="md:hidden">
                    <button onClick={() => setIsOpen(!isOpen)} className="focus:outline-none">
                        {isOpen ? <XIcon className="h-6 w-6" /> : <MenuIcon className="h-6 w-6" />}
                    </button>
                </div>
            </div>
            {isOpen && (
                <nav className="md:hidden bg-slate-900 p-4">
                    {navLinks.map(link => (
                        <a key={link} href={`#${link}`} onClick={() => setIsOpen(false)} className="block py-2 text-center capitalize hover:bg-slate-800 rounded-md transition-colors duration-300">
                            {link}
                        </a>
                    ))}
                </nav>
            )}
        </header>
    );
};

const Hero = ({ data, onImageChange }) => {
    const fileInputRef = React.useRef(null);

    const handleImageClick = () => {
        fileInputRef.current.click();
    };

    const handleFileChange = (event) => {
        const file = event.target.files[0];
        if (file) {
            const reader = new FileReader();
            reader.onload = (e) => {
                onImageChange(e.target.result);
            };
            reader.readAsDataURL(file);
        }
    };

    return (
        <section id="hero" className="min-h-screen flex items-center bg-slate-900 pt-24 pb-12">
            <div className="container mx-auto flex flex-col-reverse md:flex-row items-center justify-center text-center md:text-left px-4">
                <div className="md:w-3/5">
                    <h1 className="text-4xl md:text-6xl font-black text-white leading-tight mb-4">
                        Hi, I'm <span className="text-teal-400">{data.name}</span>
                    </h1>
                    <h2 className="text-xl md:text-2xl font-bold text-slate-300 mb-6">{data.headline}</h2>
                    <div className="flex justify-center md:justify-start space-x-4">
                        <a href={data.contact.resumeLink} target="_blank" rel="noopener noreferrer" className="bg-teal-500 hover:bg-teal-600 text-white font-bold py-3 px-6 rounded-lg transition-transform duration-300 transform hover:scale-105">
                            View Resume
                        </a>
                        <a href="#contact" className="bg-slate-700 hover:bg-slate-600 text-white font-bold py-3 px-6 rounded-lg transition-transform duration-300 transform hover:scale-105">
                            Contact Me
                        </a>
                    </div>
                </div>
                <div className="md:w-2/5 mb-10 md:mb-0 flex justify-center">
                    <div className="relative group w-64 h-64 md:w-80 md:h-80">
                        <div className="absolute inset-0 bg-teal-500 rounded-full blur-2xl opacity-40 group-hover:opacity-60 transition-opacity duration-300"></div>
                        <img src={data.profileImg} alt={data.name} className="relative w-full h-full object-cover rounded-full border-4 border-slate-700 shadow-lg" />
                        <button onClick={handleImageClick} className="absolute bottom-2 right-2 bg-slate-800/80 text-white p-2 rounded-full opacity-0 group-hover:opacity-100 transition-opacity duration-300 text-xs">
                            Update
                        </button>
                        <input type="file" ref={fileInputRef} onChange={handleFileChange} className="hidden" accept="image/*" />
                    </div>
                </div>
            </div>
        </section>
    );
};

const About = ({ content }) => (
    <Section id="about" title="About Me">
        <p className="max-w-3xl mx-auto text-center text-lg leading-relaxed text-slate-400">
            {content}
        </p>
    </Section>
);

const Skills = ({ skills }) => (
    <Section id="skills" title="My Skills">
        <div className="space-y-8">
            {Object.entries(skills).map(([category, skillList]) => (
                <div key={category}>
                    <h3 className="text-xl font-semibold text-slate-200 mb-4 capitalize text-center">{category}</h3>
                    <div className="flex flex-wrap justify-center gap-3">
                        {skillList.map(skill => <SkillBadge key={skill} skill={skill} />)}
                    </div>
                </div>
            ))}
        </div>
    </Section>
);

const Projects = ({ projects }) => (
    <Section id="projects" title="Projects">
        <div className="grid md:grid-cols-2 gap-8">
            {projects.map(project => (
                <div key={project.title} className="bg-slate-800 p-6 rounded-lg shadow-lg hover:shadow-teal-500/20 transition-shadow duration-300 flex flex-col">
                    <h3 className="text-xl font-bold text-slate-100 mb-2">{project.title}</h3>
                    <p className="text-slate-400 mb-4 flex-grow">{project.description}</p>
                    <div className="flex flex-wrap gap-2 mb-4">
