                <a href={cert.link} key={cert.title} target="_blank" rel="noopener noreferrer" className="block bg-slate-800 p-5 rounded-lg hover:bg-slate-700/80 transition-colors duration-300">
                    <h3 className="font-bold text-slate-100">{cert.title}</h3>
                    <p className="text-sm text-slate-400">{cert.issuer}</p>
