const Contact = ({ contact }) => (
    <Section id="contact" title="Get In Touch">
        <div className="max-w-xl mx-auto text-center">
            <p className="text-lg text-slate-400 mb-6">
                I'm currently open to new opportunities. Feel free to reach out via email or connect with me on social media.
            </p>
            <a href={`mailto:${contact.email}`} className="text-2xl font-bold text-teal-400 hover:text-teal-300 transition-colors duration-300">
                {contact.email}
            </a>
            <div className="flex justify-center space-x-6 mt-8">
                <a href={contact.github} target="_blank" rel="noopener noreferrer" className="text-slate-400 hover:text-teal-400 transition-colors duration-300">
                    <GithubIcon className="w-8 h-8" />
                </a>
                <a href={contact.linkedin} target="_blank" rel="noopener noreferrer" className="text-slate-400 hover:text-teal-400 transition-colors duration-300">
                    <LinkedinIcon className="w-8 h-8" />
                </a>
            </div>
