const Footer = ({ contact }) => (
    <footer className="bg-slate-900 border-t border-slate-800 py-6 text-center text-slate-500">
        <div className="container mx-auto">
            <p>&copy; {new Date().getFullYear()} {portfolioData.name}. All rights reserved.</p>
            <p>Designed & Built by Yash Malviya</p>
        </div>
    </footer>
);

// --- Main App Component ---
export default function App() {
    const [profilePicture, setProfilePicture] = useState(portfolioData.profileImg);

    const handleImageUpdate = (newImage) => {
        setProfilePicture(newImage);
        // We'll update a copy of the portfolioData for the Hero component
        // This is a simple state update, not persisting the change
    };
    
    // Create a mutable copy of data for the hero to update image
    const heroData = { ...portfolioData, profileImg: profilePicture };

    return (
        <div className="bg-slate-900 text-slate-300 font-sans leading-normal tracking-tight">
            <Header />
            <main>
                <Hero data={heroData} onImageChange={handleImageUpdate} />
                <About content={portfolioData.about} />
                <Skills skills={portfolioData.skills} />
                <Projects projects={portfolioData.projects} />
                <Experience experiences={portfolioData.experience} />
                <Education education={portfolioData.education} />
                <Certificates certificates={portfolioData.certificates} />
                <Contact contact={portfolioData.contact} />
            </main>
            <Footer contact={portfolioData.contact} />
        </div>
    );
}
